import Card from '../UI/Card';
import ExpenseItem from './ExpenseItem'
import './Expenses.css'
import ExpensesFilter from "../filter/ExpensesFilter";
import React, { useState } from 'react';

function Expenses(props){

  const [filteredYear, setFilteredYear] = useState('2020');

  const onChangeFilter = selectedItem=>{
    setFilteredYear(selectedItem);
     console.log(selectedItem);
  }

    return (
 <div> 
    <Card className = "expenses">
    <ExpensesFilter selected = {filteredYear} onChangeFilter = {onChangeFilter} />
    <ExpenseItem title = {props.items[0].title}
    amount = {props.items[0].amount}
    date = {props.items[0].date} />
    <ExpenseItem title = {props.items[1].title}
    amount = {props.items[1].amount}
    date = {props.items[1].date}/>
    <ExpenseItem title = {props.items[2].title}
    amount = {props.items[2].amount}
    date = {props.items[2].date}/>
    </Card>
  </div>
    );
}
export default Expenses;
